interpolationSearch = lambda a, n, b = 0, t = None: interpolationSearch(a,n,b,len(a) - 1) if t == None else -1 if n < a[b] or n > a[t] else b + int((t - b) * ((n - a[b]) / (a[t] - a[b]))) if a[b + int((t - b) * ((n - a[b]) / (a[t] - a[b])))] == n else interpolationSearch(a,n,b + int((t - b) * ((n - a[b]) / (a[t] - a[b]))) + 1, t) if a[b + int((t - b) * ((n - a[b]) / (a[t] - a[b])))] < n else interpolationSearch(a,n,b,t - 1)